    data() {
      return {
        btnStep: {
          angry: 0,
          paggro: 0,
          polite: 0,
          pirate: 0
        },
        prevStep: {
          angry: -1,
          paggro: -1,
          polite: -1,
          pirate: -1,
        },
        angryTracker: 0,
        noActivated: false,
        start: false,
        test: 'it be test',
        responses: [
          {text: 'response text one', value: 'one', icon: heart},
          {text: 'response text two', value: 'two', icon: logoFacebook}
        ],
        testString: `Hey mate, <br />
                      a test.`
      }
    },


    methods: {
      cycleEmoji(tone, type){
        if(type==='add'){
          if(this.btnStep[tone]<10){
          this.btnStep[tone]++
          this.prevStep[tone]++
          } 
        } else if (type==='sub'){
          if(this.btnStep[tone]>0){
          this.btnStep[tone]--
          this.prevStep[tone]--
          }
        }
      },

      angrier() {
        this.angryTracker++
      },

    },

}
