<template>


     <ion-range :class="[additional ? rangeClass : 'one', tone]"
        class="main-range"
        id="slider"
        :value="rangeVal"
        min="0"
        max="10">

         <ion-icon 
                v-if="subIsDisabled===false||adding===false"
                slot="start" :icon="removeCircle"
                @click="touchEvent($event, 'stop', 'sub')"
                class="left"
        ></ion-icon>
        <ion-icon v-else
                slot="start" :icon="removeCircle"
                class="disabled left"
                >
                
        </ion-icon>
         <ion-icon 
                 v-if="addIsDisabled===false||adding===false"
                slot="end" :icon="addCircle"
                @click="touchEvent($event, 'stop', 'add')"
                class="right"
                color=tone
                ></ion-icon>
           <ion-icon v-else
                slot="end" :icon="addCircle"
                class="disabled right">
        </ion-icon>
        </ion-range>
    
</template>

<script>

import { IonRange, IonIcon } from '@ionic/vue';
import { addCircle, removeCircle } from 'ionicons/icons'
import { useStore } from 'vuex'
import { ref } from 'vue'

export default {

    props: {
        
        tone: {
            type: String,
            required: true
        },

        sliderVal: {
            type: Boolean,
            required: true
        },

    
    },
    emits: ['update:getMoods'],
//you might need to look at the 'watcheffect' stuff
    components: {
        IonRange,
        IonIcon
    },

    setup(props) {

        // console.log('mood limit is: ' + props.moodLimit)
      
        const initVal = ref(0)
        const store = useStore()
        const firstTone = store.state.starterTones[0]
        const secondTone = store.state.starterTones[1]
        const cachedRangeVal = ref(0)
            if (firstTone===props.tone) {
                // console.log('first tone match! tone: ' + firstTone)
                initVal.value++
                // console.log('first tone match! initval: ' + initVal.value)
            }
            if (secondTone===props.tone) {
                // console.log('second tone match! tone: ' +  secondTone)
                initVal.value++
                // console.log('second tone match! initval: ' + initVal.value)
            }
        cachedRangeVal.value = initVal.value

        return {
            addCircle, 
            removeCircle,
            firstTone,
            secondTone,
            initVal: initVal.value,
        }
    },

    mounted(){
        if(this.initVal){
            this.setInit=true
        }
    },

    data() {
        return {
          
            rangeVal: this.initVal,
            //! fix subs not being disabled when initial moods go in
            subIsDisabled: null,
            addIsDisabled: false,
            updatingCache: null,
            setInit: false,
            adding: false,
            rangeClass: '',
            additional: false,

        }
    },

    methods: {


        touchEvent(event, optional, type){

            this.adding=true
            
            let cachedVal = this.rangeVal
    
            if(!optional){
                
                this.rangeVal = event.target.value

            } else if (optional) {
             
                if(type==='add') {
                        // console.log(this.rangeVal)
                        let newAdd = this.rangeVal + 1
                        this.rangeVal = newAdd
                } else if (type==='sub'){
                        let newSub = this.rangeVal - 1
                        this.rangeVal = newSub
                }

                } else {
                    console.log('touchEvent: something went wrong')
                }

                    if (this.rangeVal === cachedVal){
                        console.log('not enough range change')
                        this.adding=false
                        return

                        } else {

                        let active = this.tone
                        this.$store.state.activeMood=active

                        this.$emit('update:getMoods', {
                                val: this.rangeVal,
                                tone: this.tone,
                        });
                        this.knobCheck(this.rangeVal)
                        this.adding=false

            }
           
            //! will need to update to ignore the initthingy events
        },

        buttonRangeChange(type) {

                let active = this.tone
                this.$store.state.activeMood=active
         

                if(type==="add") {
                    this.rangeVal++
                    // this.touchEvent()
                } else if (type==="sub") {
                    this.rangeVal--
                    // this.touchEvent()
                } else {
                    console.log('rangeval, something went wrong')
                }
         
        },

        knobCheck(num){
                if(num>=3&&num<6){
                    this.rangeClass='two'
                    this.additional = true
                } else if (num>=6&&num<10){
                    this.rangeClass='three'
                    this.additional = true
                } else if (num===10){
                    this.rangeClass='four'
                    this.additional = true
                } else {
                    this.additional = false
                }
        },

    },


    watch: {

        sliderVal: {

            immediate: true,

            handler(newVal){

                if(newVal===true){
                    let subme = this.$store.state.sliderSubs[this.tone]
                    // console.log('subme is: ' + subme + 'at ' + this.tone)
                    let updatedVal = this.rangeVal - subme
                    this.rangeVal = updatedVal
                }
            }
        },

        rangeVal: {

            immediate: true,
             handler(newVal) {
             if(newVal<=0){
                this.subIsDisabled=true
                // console.log('disabled style applied')
            } else {
                this.subIsDisabled=false
            }

            if(this.newVal>=9){
                this.addIsDisabled=true
                } else {
                this.addIsDisabled=false
                }
            }

        },
       
    },
}
</script>

